mapper.select:
        SELECT


--         级联表的字段
        ,shipin.shipin_name as shipinName
        ,shipin.shipin_types as shipinTypes
        ,shipin.shipin_photo as shipinPhoto
        ,shipin.shipin_video as shipinVideo
        ,shipin.zan_number as zanNumber
        ,shipin.cai_number as caiNumber
        ,shipin.shipin_content as shipinContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_email as yonghuEmail
        ,yonghu.yonghu_delete as yonghuDelete

        FROM shipin_collection  a
        left JOIN shipin shipin ON a.shipin_id = shipin.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and (
                    a.shipin_id = #{params.shipinId}
                )


                and (
                    a.yonghu_id = #{params.yonghuId}
                )


                and a.shipin_collection_types = #{params.shipinCollectionTypes}


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




                and a.shipin_id IS NOT NULL


                and shipin.shipin_name like CONCAT('%',#{params.shipinName},'%')


                and shipin.shipin_types = #{params.shipinTypes}



                and shipin.shipin_video like CONCAT('%',#{params.shipinVideo},'%')


                  and shipin.zan_number >= #{params.zanNumberStart}   


                  and shipin.zan_number <= #{params.zanNumberEnd}   


                and shipin.zan_number = #{params.zanNumber}


                  and shipin.cai_number >= #{params.caiNumberStart}   


                  and shipin.cai_number <= #{params.caiNumberEnd}   


                and shipin.cai_number = #{params.caiNumber}


                and shipin.shipin_content like CONCAT('%',#{params.shipinContent},'%')


                  and UNIX_TIMESTAMP(shipin.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(shipin.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')


                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


                and yonghu.yonghu_delete = #{params.yonghuDelete}


                  and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 



        order by a.${params.orderBy} desc
