mapper.select:
SELECT
-- 级联表的字段 ,wenzhang.wenzhang_name as wenzhangName ,wenzhang.wenzhang_types as wenzhangTypes ,wenzhang.wenzhang_photo as wenzhangPhoto ,wenzhang.wenzhang_file as wenzhangFile ,wenzhang.zan_number as zanNumber ,wenzhang.cai_number as caiNumber ,wenzhang.wenzhang_content as wenzhangContent ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_delete as yonghuDelete
FROM wenzhang_liuyan a left JOIN wenzhang wenzhang ON a.wenzhang_id = wenzhang.id left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.wenzhang_id = #{params.wenzhangId} )
and ( a.yonghu_id = #{params.yonghuId} )
and a.wenzhang_liuyan_text like CONCAT('%',#{params.wenzhangLiuyanText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.reply_text like CONCAT('%',#{params.replyText},'%')
and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart})
and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd})
and a.wenzhang_id IS NOT NULL
and wenzhang.wenzhang_name like CONCAT('%',#{params.wenzhangName},'%')
and wenzhang.wenzhang_types = #{params.wenzhangTypes}
and wenzhang.zan_number >= #{params.zanNumberStart}
and wenzhang.zan_number <= #{params.zanNumberEnd}
and wenzhang.zan_number = #{params.zanNumber}
and wenzhang.cai_number >= #{params.caiNumberStart}
and wenzhang.cai_number <= #{params.caiNumberEnd}
and wenzhang.cai_number = #{params.caiNumber}
and wenzhang.wenzhang_content like CONCAT('%',#{params.wenzhangContent},'%')
and UNIX_TIMESTAMP(wenzhang.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(wenzhang.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
and UNIX_TIMESTAMP(yonghu.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(yonghu.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
order by a.${params.orderBy} desc
